p1 = function() {

  sequencia_numeros = seq(999)
  mult_3 = NULL
  mult_5 = NULL

  for (i in sequencia_numeros) {

    if(i %% 3 == 0) {

      mult_3 = c(mult_3, i)

    } else if (i %% 5 == 0) {

      mult_5 = c(mult_5, i)

    }

  }

  return(sum(c(mult_3, mult_5)))

}

